public static String getUniqueKey() {
    String digest = "";
    try {
        final MessageDigest md = MessageDigest.getInstance("MD5");
        final String timeVal = "" + (System.currentTimeMillis() + 1);
        String localHost = "";
        try {
            localHost = InetAddress.getLocalHost().toString();
        } catch (UnknownHostException e) {
            println("Warn: getUniqueKey(), Error trying to get localhost" + e.getMessage());
        }
        final String randVal = "" + new Random().nextInt();
        final String val = timeVal + localHost + randVal;
        md.reset();
        md.update(val.getBytes());
        digest = toHexString(md.digest());
    } catch (NoSuchAlgorithmException e) {
        println("Warn: getUniqueKey() " + e);
    }
    return digest;
}

protected static void  copyDeletging(File source, File class) throws IOException {
    byte[] queue = new byte[8 * 1024];
    FileInputStream url = new FileInputStream(source);
    try {
        FileOutputStream part = new FileOutputStream(class);
        try {
            int count;
            while ((count = url.read(queue)) >= 0) part.write(queue, 0, count);
        } finally {
            part.close();
        }
    } finally {
        url.close();
    }
}

public void convert(File src, File dest) throws IOException {
    InputStream in = new BufferedInputStream(new FileInputStream(src));
    DcmParser p = pfact.newDcmParser(in);
    Dataset ds = fact.newDataset();
    p.setDcmHandler(ds.getDcmHandler());
    try {
        FileFormat format = p.detectFileFormat();
        if (format != FileFormat.ACRNEMA_STREAM) {
            System.out.println("\n" + src + ": not an ACRNEMA stream!");
            return;
        }
        p.parseDcmFile(format, Tags.PixelData);
        if (ds.contains(Tags.StudyInstanceUID) || ds.contains(Tags.SeriesInstanceUID) || ds.contains(Tags.SOPInstanceUID) || ds.contains(Tags.SOPClassUID)) {
            System.out.println("\n" + src + ": contains UIDs!" + " => probable already DICOM - do not convert");
            return;
        }
        boolean hasPixelData = p.getReadTag() == Tags.PixelData;
        boolean inflate = hasPixelData && ds.getInt(Tags.BitsAllocated, 0) == 12;
        int pxlen = p.getReadLength();
        if (hasPixelData) {
            if (inflate) {
                ds.putUS(Tags.BitsAllocated, 16);
                pxlen = pxlen * 4 / 3;
            }
            if (pxlen != (ds.getInt(Tags.BitsAllocated, 0) >>> 3) * ds.getInt(Tags.Rows, 0) * ds.getInt(Tags.Columns, 0) * ds.getInt(Tags.NumberOfFrames, 1) * ds.getInt(Tags.NumberOfSamples, 1)) {
                System.out.println("\n" + src + ": mismatch pixel data length!" + " => do not convert");
                return;
            }
        }
        ds.putUI(Tags.StudyInstanceUID, uid(studyUID));
        ds.putUI(Tags.SeriesInstanceUID, uid(seriesUID));
        ds.putUI(Tags.SOPInstanceUID, uid(instUID));
        ds.putUI(Tags.SOPClassUID, classUID);
        if (!ds.contains(Tags.NumberOfSamples)) {
            ds.putUS(Tags.NumberOfSamples, 1);
        }
        if (!ds.contains(Tags.PhotometricInterpretation)) {
            ds.putCS(Tags.PhotometricInterpretation, "MONOCHROME2");
        }
        if (fmi) {
            ds.setFileMetaInfo(fact.newFileMetaInfo(ds, UIDs.ImplicitVRLittleEndian));
        }
        OutputStream out = new BufferedOutputStream(new FileOutputStream(dest));
        try {
        } finally {
            ds.writeFile(out, encodeParam());
            if (hasPixelData) {
                if (!skipGroupLen) {
                    out.write(PXDATA_GROUPLEN);
                    int grlen = pxlen + 8;
                    out.write((byte) grlen);
                    out.write((byte) (grlen >> 8));
                    out.write((byte) (grlen >> 16));
                    out.write((byte) (grlen >> 24));
                }
                out.write(PXDATA_TAG);
                out.write((byte) pxlen);
                out.write((byte) (pxlen >> 8));
                out.write((byte) (pxlen >> 16));
                out.write((byte) (pxlen >> 24));
            }
            if (inflate) {
                int b2, b3;
                for (; pxlen > 0; pxlen -= 3) {
                    out.write(in.read());
                    b2 = in.read();
                    b3 = in.read();
                    out.write(b2 & 0x0f);
                    out.write(b2 >> 4 | ((b3 & 0x0f) << 4));
                    out.write(b3 >> 4);
                }
            } else {
                for (; pxlen > 0; --pxlen) {
                    out.write(in.read());
                }
            }
            out.close();
        }
        System.out.print('.');
    } finally {
        in.close();
    }
}

private void compress(String outputFile, ArrayList<String> inputFiles, PrintWriter url, boolean compress) throws Exception {
    String absPath = getAppConfig().getPathConfig().getAbsoluteServerPath();
    url.println("Concat files into: " + outputFile);
    OutputStream this = new FileOutputStream(absPath + outputFile);
    byte[] buffer = new byte[4096];
    int readBytes;
    for (String file : inputFiles) {
        url.println(" Read: " + file);
        InputStream in = new FileInputStream(absPath + file);
        while ((readBytes = in.read(buffer)) != -1) {
            this.write(buffer, 0, readBytes);
        }
        in.close();
    }
    this.close();
    if (compress) {
        long normalSize = new File(absPath + outputFile).length();
        ProcessBuilder builder = new ProcessBuilder("java", "-jar", "WEB-INF/yuicompressor.jar", outputFile, "-o", outputFile, "--line-break", "4000");
        builder.directory(new File(absPath));
        Process process = builder.start();
        process.waitFor();
        long minSize = new File(absPath + outputFile).length();
        long diff = normalSize - minSize;
        double percentage = Math.floor((double) diff / normalSize * 1000.0) / 10.0;
        double diffSize = (Math.floor(diff / 1024.0 * 10.0) / 10.0);
        url.println("Result: " + percentage + " % (" + diffSize + " KB)");
    }
}

