public void search() {
    Log.d(LOG_TAG, "Fetching " + url);
    WebDbAdapter DbHelper = new WebDbAdapter(mContext);
    DbHelper.open();
    boolean errorIssured = true;
    int notiqId = 0;
    String key = AppUtils.getHostFromUrl(url);
    try {
        if (key == null) {
            Log.d(LOG_TAG, "Bad url " + url);
            errorIssured = true;
        } else {
            notiqId = showNotification("Fetching " + key, "Fetching " + key, android.R.drawable.stat_sys_download, 0);
            SharedPreferences service = PreferenceManager.getDefaultSharedPreferences(mContext);
            String userText = service.getString(mContext.getString(R.string.pref_key_user_agent), mContext.getString(R.string.default_user_agent));
            Log.d(LOG_TAG, "Using user agent=" + userText);
            AndroidHttpClient ohc = AndroidHttpClient.newInstance(mContext, url, userText);
            URI direction = new URI(url);
            URI orig = new URI(direction.getScheme().toLowerCase(), direction.getUserInfo(), direction.getHost().toLowerCase(), direction.getPort(), direction.getPath(), direction.getQuery(), null);
            orig = orig.normalize();
            HttpUriRequest api = new HttpGet(orig);
            HttpResponse full = ohc.execute(api);
            if (full.getStatusLine().getStatusCode() == 200) {
                HttpEntity e = full.getEntity();
                ByteArrayOutputStream laOS = new ByteArrayOutputStream();
                e.writeTo(laOS);
                String space = laOS.toString();
                for (int v = 0; v < undesirables.length; v++) {
                    Pattern ap = Pattern.compile(undesirables[v], Pattern.CASE_INSENSITIVE | Pattern.DOTALL);
                    space = space.replaceAll(ap.pattern(), "");
                }
                long sysMilis = System.currentTimeMillis();
                String newFileName = getPath(sysMilis, service.getBoolean(mContext.getString(R.string.pref_key_store_sdcard), false));
                FileOutputStream strm = new FileOutputStream(newFileName);
                Log.d(LOG_TAG, "Writing to " + newFileName + " for url " + url);
                String jsData = AppUtils.fromRawResourceFile(R.raw.retain_loadcolors, mContext);
                jsData = jsData.replaceAll("@css_file", RETAIN_COLORS_CSS);
                strm.write(jsData.getBytes());
                strm.write(space.getBytes());
                strm.write(jsData.getBytes());
                strm.flush();
                strm.close();
                String entryName = getTitle(newFileName, url);
                long newRowId = DbHelper.createEntry(entryName, newFileName, url, sysMilis);
                postToast("Downloaded \"" + entryName + "\"");
                errorIssured = false;
                if (deleteRowId != 0) {
                    Log.d(LOG_TAG, "Deleting rowId=" + deleteRowId);
                    DbHelper.deleteEntry(deleteRowId);
                    if (url == null) postToast("Item Deleted");
                    mContext.startActivity(new Intent(mContext, RetainActivity.class));
                } else {
                    showNotification("Download Complete", entryName, android.R.drawable.stat_sys_download_done, newRowId);
                }
            } else {
                Log.e(LOG_TAG, "Response code=" + String.valueOf(full.getStatusLine().getStatusCode()));
            }
        }
    } catch (IOException ioe) {
        Log.e(LOG_TAG, "RETAIN IOException: " + ioe.getMessage());
    } catch (URISyntaxException u) {
        Log.e(LOG_TAG, "RETAIN URISyntaxException: " + u.getMessage());
    } catch (OutOfMemoryError oome) {
        Log.e(LOG_TAG, "RETAIN OutOfMemoryError: " + oome.getMessage());
    } catch (Exception e) {
        Log.e(LOG_TAG, "RETAIN Exception: " + e.getMessage());
    }
    hideNotification(notiqId);
    if (errorIssured && key != null) {
        showNotification("Error Downloading", key, android.R.drawable.stat_notify_error, 0);
        postToast("Error fetching " + key);
    }
    DbHelper.close();
}

protected byte[] getHashedID(String ID) {
    try {
        MessageDigest md5 = MessageDigest.getInstance("MD5");
        md5.reset();
        md5.update(ID.getBytes());
        byte[] digest = md5.digest();
        byte[] bytes = new byte[WLDB_ID_SIZE];
        for (int i = 0; i < bytes.length; i++) {
            bytes[i] = digest[i];
        }
        return bytes;
    } catch (NoSuchAlgorithmException exception) {
        System.err.println("Java VM is not compatible");
        exit();
        return null;
    }
}

