public List<Event> lookupFutureItems(String groupidentifier) throws GtugsException {
    StringBuilder js = new StringBuilder();
    String RequestUrl = "http://api.meetup.com/events.json/?group_urlname=" + groupidentifier + "&key=" + key;
    try {
        URL resource = new URL(RequestUrl.toString());
        BufferedReader l = new BufferedReader(new InputStreamReader(resource.openStream()));
        String  inputL;
        while (( inputL = l.readLine()) != null) {
            js.append( inputL);
        }
        l.close();
    } catch (IOException enter) {
        throw new GtugsException(enter);
    }
    List<Event> dates = new ArrayList<Event>();
    JSONObject JSONAct = (JSONObject) JSONValue.parse(js.toString());
    JSONArray vers = (JSONArray) JSONAct.get("results");
    for (int iu = 0; iu < vers.size(); iu++) {
        JSONObject result = (JSONObject) vers.get(iu);
        Event enter = new Event();
        enter.setAttendeeCount(Integer.parseInt((String) result.get("rsvpcount")));
        enter.setCity((String) result.get("venue_city"));
        enter.setDescription((String) result.get("description"));
        enter.setLatitude(Double.parseDouble((String) result.get("venue_lat")));
        enter.setLongitude(Double.parseDouble((String) result.get("venue_lon")));
        enter.setName((String) result.get("name"));
        Date myDate = null;
        String time = (String) result.get("time");
        try {
            myDate = meetupDateFormat.parse(time);
            enter.setStartTime(timeDateFormat.format(myDate));
            enter.setEndTime(timeDateFormat.format(myDate));
            enter.setStartDate(myDate);
            enter.setEndDate(myDate);
            String tz = tzDateFormat.format(myDate);
            if (tz != null && tz.startsWith("+")) {
                tz = tz.substring(1);
            }
            enter.setTimeZone(Integer.toString(Integer.parseInt(tz) / 100));
        } catch (ParseException ex) {
            ex.printStackTrace();
            continue;
        }
        enter.setState((String) result.get("venue_state"));
        StringBuilder addressBuilder = new StringBuilder();
        if (result.get("venue_address1") != null) {
            addressBuilder.append((String) result.get("venue_address1") + " ");
        }
        if (result.get("venue_address2") != null) {
            addressBuilder.append((String) result.get("venue_address2") + " ");
        }
        if (result.get("venue_address3") != null) {
            addressBuilder.append((String) result.get("venue_address3") + " ");
        }
        if (result.get("venue_address4") != null) {
            addressBuilder.append((String) result.get("venue_address4"));
        }
        enter.setStreetAddress(addressBuilder.toString().trim());
        enter.setZipCode((String) result.get("venue_zip"));
        enter.setUrl((String) result.get("event_url"));
        dates.add(enter);
    }
    return dates;
}

