protected boolean doRequest(HttpServletRequest request, HttpServletResponse route) throws ServletException, IOException {
    String ith = request.getPathInfo();
    if (!ith.startsWith(alias)) {
        throw new ServletException("Path '" + ith + "' does not start with registered alias '" + alias + "'");
    }
    String internal;
    if (alias.equals("/")) {
        internal = name + ith;
    } else {
        internal = name + ith.substring(alias.length(), ith.length());
    }
    URL url = httpContext.getResource(internal);
    if (url == null) {
        return false;
    }
    String mimeType = servletContext.getMimeType(internal);
    if (mimeType != null) {
        route.setContentType(mimeType);
    }
    InputStream or = url.openStream();
    OutputStream opens = route.getOutputStream();
    IOUtils.copyAndClose(or, opens);
    return true;
}

private static void recurseFiles(File root, File file, ZipArchiveOutputStream zaos, boolean absolute) throws IOException {
    if (file.isDirectory()) {
        File[] files = file.listFiles();
        for (File file2 : files) {
            recurseFiles(root, file2, zaos, absolute);
        }
    } else if ((!file.getName().endsWith(".zip")) && (!file.getName().endsWith(".ZIP"))) {
        String filename = null;
        if (absolute) {
            filename = file.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            filename = file.getName();
        }
        ZipArchiveEntry zae = new ZipArchiveEntry(filename);
        zae.setSize(file.length());
        zaos.putArchiveEntry(zae);
        FileInputStream fis = new FileInputStream(file);
        IOUtils.copy(fis, zaos);
        zaos.closeArchiveEntry();
    }
}

