protected static void  copyDeletging(File service, File folder) throws IOException {
    byte[] queue = new byte[8 * 1024];
    FileInputStream url = new FileInputStream(service);
    try {
        FileOutputStream part = new FileOutputStream(folder);
        try {
            int count;
            while ((count = url.read(queue)) >= 0) part.write(queue, 0, count);
        } finally {
            part.close();
        }
    } finally {
        url.close();
    }
}

