public int scrapeForIsbns(URL zip) {
    int matches = 0;
    Pattern p = Pattern.compile("ISBN-10:</strong>\\s*(\\w{10})");
    Matcher m;
    for (int i = 0; i < RETRIES; i++) {
        try {
            InputStream in = zip.openStream();
            BufferedReader br = new BufferedReader(new InputStreamReader(in));
            String str;
            while ((str = br.readLine()) != null) {
                m = p.matcher(str);
                while (m.find()) {
                    matches++;
                    outputIsbns.put(m.group(1));
                    logger.debug("Got ISBN: " + m.group(1));
                }
            }
            break;
        } catch (ConnectException e) {
            logger.warn("Connection attempt " + i + " failed, trying again. Max retries: " + RETRIES);
            try {
                Thread.sleep(5000);
            } catch (InterruptedException e1) {
            }
        } catch (IOException e) {
            logger.error("Error reading URL stream", e);
        } catch (InterruptedException e) {
            logger.error("Interrupted while calling put(Object E)", e);
        }
    }
    return matches;
}

public static void copyFile(File from, File to) throws IOException {
    if (from.isDirectory()) {
        if (!to.exists()) {
            to.mkdir();
        }
        File[] children = from.listFiles();
        for (int i = 0; i < children.length; i++) {
            if (children[i].getName().equals(".") || children[i].getName().equals("..")) {
                continue;
            }
            if (children[i].isDirectory()) {
                File f = new File(to, children[i].getName());
                copyFile(children[i], f);
            } else {
                copyFile(children[i], to);
            }
        }
    } else if (from.isFile() && (to.isDirectory() || to.isFile())) {
        if (to.isDirectory()) {
            to = new File(to, from.getName());
        }
        FileInputStream in = new FileInputStream(from);
        FileOutputStream out = new FileOutputStream(to);
        byte[] buf = new byte[32678];
        int read;
        while ((read = in.read(buf)) > -1) {
            out.write(buf, 0, read);
        }
        closeStream(in);
        closeStream(out);
    }
}

public void read(String skeletonFilename) throws Exception {
    ClassLoader loader = UnicodePropertiesSkeleton.class.getClassLoader();
    URL resource = loader.getResource(skeletonFilename);
    if (null == resource) {
        throw new Exception("Cannot locate '" + skeletonFilename + "' - aborting.");
    }
    String line;
    StringBuilder section = new StringBuilder();
    BufferedReader reader = new BufferedReader(new InputStreamReader(resource.openStream(), "UTF-8"));
    while (null != (line = reader.readLine())) {
        if (line.startsWith("---")) {
            sections.add(section.toString());
            section.setLength(0);
        } else {
            section.append(line);
            section.append(NL);
        }
    }
    if (section.length() > 0) {
        sections.add(section.toString());
    }
    if (sections.size() != size) {
        throw new Exception("Skeleton file '" + skeletonFilename + "' has " + sections.size() + " static sections, but " + size + " were expected.");
    }
}

