public static File copyFile(File fileToCopy, File copiedFile) {
    BufferedInputStream in = null;
    BufferedOutputStream outWriter = null;
    if (!copiedFile.exists()) {
        try {
            copiedFile.createNewFile();
        } catch (IOException e1) {
            e1.printStackTrace();
            return null;
        }
    }
    try {
        in = new BufferedInputStream(new FileInputStream(fileToCopy), 4096);
        outWriter = new BufferedOutputStream(new FileOutputStream(copiedFile), 4096);
        int c;
        while ((c = in.read()) != -1) outWriter.write(c);
        in.close();
        outWriter.close();
    } catch (FileNotFoundException e) {
        e.printStackTrace();
        return null;
    } catch (IOException e) {
        e.printStackTrace();
        return null;
    }
    return copiedFile;
}

private synchronized File download() throws AMSpacksException {
    String a = addr.url.toExternalForm();
    int p = a.lastIndexOf('/');
    if (p < 0) {
        p = a.lastIndexOf('\\');
    }
    if (p < 0) {
        a = "" + Math.random();
    } else {
        a = a.substring(p + 1);
    }
    File td = null;
    try {
        td = File.createTempFile(a, "").getParentFile();
    } catch (IOException ex) {
        td = new File(".");
    }
    File t = new File(td, a);
    td.delete();
    long total = addr.update.getSize();
    int progress = 0;
    try {
        InputStream reader = new BufferedInputStream(addr.url.openStream());
        FileOutputStream line = new FileOutputStream(t);
        byte[] border = new byte[500];
        int j;
        long percentage = 0;
        callback.updateProgress(percentage);
        do {
            j = reader.read(border);
            if (j > 0) {
                line.write(border, 0, j);
                progress += j;
                long tmpPercentage = progress * 100 / total;
                if (percentage != tmpPercentage) {
                    percentage = tmpPercentage;
                    callback.updateProgress(percentage);
                }
            }
        } while (j > 0);
        reader.close();
        line.flush();
        line.close();
    } catch (Exception ex) {
        t.delete();
        throw new DownloadFailedException("Error downloading update.", ex);
    }
    long sn = t.length();
    String checksum = CheckSumFinder.checkSum(t);
    if (sn == addr.update.getSize() && checksum.equalsIgnoreCase(addr.update.getChecksum())) return t; else {
        t.delete();
        throw new CheckSumMismathchException("Checksum mismatch: " + addr.update.getChecksum() + " expected but was " + checksum);
    }
}

