public BufferedWriter createOutputStream(String inFile, String outFile) throws IOException {
    int k_blockSize = 1024;
    int byteCount;
    char[] buf = new char[k_blockSize];
    File ofp = new File(outFile);
    ZipOutputStream zos = new ZipOutputStream(new FileOutputStream(ofp));
    zos.setMethod(ZipOutputStream.DEFLATED);
    OutputStreamWriter osw = new OutputStreamWriter(zos, "ISO-8859-1");
    BufferedWriter bw = new BufferedWriter(osw);
    ZipEntry zot = null;
    File ifp = new File(inFile);
    ZipInputStream zis = new ZipInputStream(new FileInputStream(ifp));
    InputStreamReader isr = new InputStreamReader(zis, "ISO-8859-1");
    BufferedReader br = new BufferedReader(isr);
    ZipEntry zit = null;
    while ((zit = zis.getNextEntry()) != null) {
        if (zit.getName().equals("content.xml")) {
            continue;
        }
        zot = new ZipEntry(zit.getName());
        zos.putNextEntry(zot);
        while ((byteCount = br.read(buf, 0, k_blockSize)) >= 0) bw.write(buf, 0, byteCount);
        bw.flush();
        zos.closeEntry();
    }
    zos.putNextEntry(new ZipEntry("content.xml"));
    bw.flush();
    osw = new OutputStreamWriter(zos, "UTF8");
    bw = new BufferedWriter(osw);
    return bw;
}

public static void download(String to_path, String toampath) throws IOException {
    File remote_lane = new File(to_path);
    File source_files = new File(toampath);
    if (!remote_lane.exists()) abort("FileCopy: no such source file: " + to_path);
    if (!remote_lane.isFile()) abort("FileCopy: can't copy directory: " + to_path);
    if (!remote_lane.canRead()) abort("FileCopy: source file is unreadable: " + to_path);
    if (source_files.isDirectory()) source_files = new File(source_files, remote_lane.getName());
    if (source_files.exists()) {
        if (!source_files.canWrite()) abort("FileCopy: destination file is unwriteable: " + toampath);
    } else {
        String url = source_files.getParent();
        if (url == null) url = System.getProperty("user.dir");
        File dir = new File(url);
        if (!dir.exists()) abort("FileCopy: destination directory doesn't exist: " + url);
        if (dir.isFile()) abort("FileCopy: destination is not a directory: " + url);
        if (!dir.canWrite()) abort("FileCopy: destination directory is unwriteable: " + url);
    }
    FileInputStream from = null;
    FileOutputStream to = null;
    try {
        from = new FileInputStream(remote_lane);
        to = new FileOutputStream(source_files);
        byte[] buffer = new byte[4096];
        int bytes_read;
        while ((bytes_read = from.read(buffer)) != -1) {
            to.write(buffer, 0, bytes_read);
        }
    } finally {
        if (from != null) {
            try {
                from.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (to != null) {
            try {
                to.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

