private void handleNoderegainsServices(long issueId, long siteid, String ipAttr, long serverX, String processName) {
    Category url = ThreadCategory.getInstance(OutageWriter.class);
    if (issueId == -1 || siteid == -1 || ipAttr == null || serverX == -1) {
        url.warn(EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + " ignored - info incomplete - eventid/nodeid/ip/svc: " + issueId + "/" + siteid + "/" + ipAttr + "/" + serverX);
        return;
    }
    Connection logconn = null;
    try {
        logconn = DatabaseConnectionFactory.getInstance().getConnection();
        if (openOutageExists(logconn, siteid, ipAttr, serverX)) {
            try {
                logconn.setAutoCommit(false);
            } catch (SQLException sqle) {
                url.error("Unable to change database AutoCommit to FALSE", sqle);
                return;
            }
            PreparedStatement outageUpdatedATER = logconn.prepareStatement(OutageConstants.DB_UPDATE_OUTAGE_FOR_SERVICE);
            outageUpdatedATER.setLong(1, issueId);
            outageUpdatedATER.setTimestamp(2, convertEventTimeIntoTimestamp(processName));
            outageUpdatedATER.setLong(3, siteid);
            outageUpdatedATER.setString(4, ipAttr);
            outageUpdatedATER.setLong(5, serverX);
            outageUpdatedATER.executeUpdate();
            outageUpdatedATER.close();
            try {
                logconn.commit();
                if (url.isDebugEnabled()) url.debug("nodeRegainedService: closed outage for nodeid/ip/service " + siteid + "/" + ipAttr + "/" + serverX + " in DB");
            } catch (SQLException se) {
                url.warn("Rolling back transaction, nodeRegainedService could not be recorded  for nodeId/ipAddr/service: " + siteid + "/" + ipAttr + "/" + serverX, se);
                try {
                    logconn.rollback();
                } catch (SQLException sqle) {
                    url.warn("SQL exception during rollback, reason", sqle);
                }
            }
        } else {
            url.warn("\'" + EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + "\' for " + siteid + "/" + ipAttr + "/" + serverX + " does not have open record.");
        }
    } catch (SQLException se) {
        url.warn("SQL exception while handling \'nodeRegainedService\'", se);
    } finally {
        try {
            if (logconn != null) logconn.close();
        } catch (SQLException e) {
            url.warn("Exception closing JDBC connection", e);
        }
    }
}

public void makeRead(String user, long databaseID, long time) throws SQLException {
    String query = "replace into fs.read_post (post, user, read_date) values (?, ?, ?)";
    ensureConnection();
    PreparedStatement statement = m_connection.prepareStatement(query);
    try {
        statement.setLong(1, databaseID);
        statement.setString(2, user);
        statement.setTimestamp(3, new Timestamp(time));
        int count = statement.executeUpdate();
        if (0 == count) throw new SQLException("Nothing updated.");
        m_connection.commit();
    } catch (SQLException e) {
        m_connection.rollback();
        throw e;
    } finally {
        statement.close();
    }
}

