        fmt.setFormat(Format.getPrettyFormat());
         webpage = new FileOutputStream(configFile);
        OutputStreamWriter writer = new OutputStreamWriter( webpage, "UTF-8");
        fmt.output(OutputDiv, writer);
    } catch (Exception e) {
        logger.log(Level.SEVERE, e.getMessage());
    } finally {
        IOUtils.closeQuietly( webpage);
    }
}

