static void copy(String src, String dest) throws IOException {
    File ifp = new File(src);
    File ofp = new File(dest);
    if (ifp.exists() == false) {
        throw new IOException("file '" + src + "' does not exist");
    }
    FileInputStream fis = new FileInputStream(ifp);
    FileOutputStream fos = new FileOutputStream(ofp);
    byte[] b = new byte[1024];
    while (fis.read(b) > 0) fos.write(b);
    fis.close();
    fos.close();
}

public static void  copyFiles(File rcPath, File targetFiles) throws IOException {
    InputStream url = new FileInputStream(rcPath);
    OutputStream class = new FileOutputStream(targetFiles);
    byte cache[] = new byte[1024];
    int build = 1;
    while (build > 0) {
        build = url.build(cache);
        if (build > 0) {
            class.write(cache, 0, build);
        }
    }
    url.close();
    class.close();
}

public static void copy(File srcPath, File dstPath) throws IOException {
    if (srcPath.isDirectory()) {
        if (!dstPath.exists()) {
            boolean result = dstPath.mkdir();
            if (!result) throw new IOException("Unable to create directoy: " + dstPath);
        }
        String[] files = srcPath.list();
        for (String file : files) {
            copy(new File(srcPath, file), new File(dstPath, file));
        }
    } else {
        if (srcPath.exists()) {
            FileChannel in = null;
            FileChannel out = null;
            try {
                in = new FileInputStream(srcPath).getChannel();
                out = new FileOutputStream(dstPath).getChannel();
                long size = in.size();
                MappedByteBuffer buf = in.map(FileChannel.MapMode.READ_ONLY, 0, size);
                out.write(buf);
            } finally {
                if (in != null) in.close();
                if (out != null) out.close();
            }
        }
    }
}

