            destCh.close();
        } catch (IOException ex) {
            Logger.getLogger(EditorUtil.class.getName()).log(Level.SEVERE, null, ex);
        }
    }
}

public String readRemoteFile() throws IOException {
    String response = "";
    boolean eof = false;
    URL resource = new URL(StaticData.remoteFile);
    InputStream is = resource.openStream();
    BufferedReader br = new BufferedReader(new InputStreamReader(is));
    String s;
    s = br.readLine();
    response = s;
    while (!eof) {
        try {
            s = br.readLine();
            if (s == null) {
                eof = true;
                br.close();
            } else response += s;
        } catch (EOFException eo) {
            eof = true;
        } catch (IOException e) {
            System.out.println("IO Error : " + e.getMessage());
        }
    }
    return response;
}

public static void copyFile(File in, File out) throws IOException {
    if (in.getCanonicalPath().equals(out.getCanonicalPath())) {
        return;
    }
    FileChannel inChannel = new FileInputStream(in).getChannel();
    FileChannel outChannel = new FileOutputStream(out).getChannel();
    try {
        inChannel.transferTo(0, inChannel.size(), outChannel);
    } catch (IOException e) {
        throw e;
    } finally {
        if (inChannel != null) {
            inChannel.close();
        }
        if (outChannel != null) {
            outChannel.close();
        }
    }
}

