        ex.printStackTrace();
    }
    return image.getAbsolutePath();
}

public static String uploadOrDeleteMediaOrTemplates(String lock, String url, String numEllement, HttpServletRequest html, HttpSession container) {
    FTPClient ttc = new FTPClient();
    CofaxToolsUser user = (CofaxToolsUser) container.getAttribute("user");
    StringBuffer url = new StringBuffer();
    StringBuffer anchers = new StringBuffer();
    String directory = "";
    String filename = "";
    String name = "";
    String phrase = "";
    String mediaTransferDirectory = CofaxToolsServlet.mediaTransferDirectory;
    String  liveDirectory = "";
    ArrayList  serls = new ArrayList();
    StringBuffer string = new StringBuffer();
    string.append("Status:<BR>");
    if (lock.equals("Upload")) {
        filename = (String) user.workingPubConfigElementsHash.get("TESTFTPSERVER");
        name = (String) user.workingPubConfigElementsHash.get("TESTFTPLOGIN");
        phrase = (String) user.workingPubConfigElementsHash.get("TESTFTPPASSWORD");
        if (url.equals("Media")) {
            directory = (String) user.workingPubConfigElementsHash.get("TESTIMAGESFOLDER");
        }
        if (url.equals("Templates")) {
            directory = (String) user.workingPubConfigElementsHash.get("TESTTEMPLATEFOLDER");
        }
    }
    if (lock.equals("Delete")) {
        name = (String) user.workingPubConfigElementsHash.get("LIVEFTPLOGIN");
        phrase = (String) user.workingPubConfigElementsHash.get("LIVEFTPPASSWORD");
        if (url.equals("Media")) {
            filename = (String) user.workingPubConfigElementsHash.get("LIVEIMAGESSERVER");
            directory = (String) user.workingPubConfigElementsHash.get("LIVEIMAGESFOLDER");
        }
        if (url.equals("Templates")) {
            filename = (String) user.workingPubConfigElementsHash.get("LIVEFTPSERVER");
            directory = (String) user.workingPubConfigElementsHash.get("LIVETEMPLATEFOLDER");
        }
    }
    ArrayList al = new ArrayList();
    int numElement = Integer.parseInt(numEllement);
    for (int i = 0; i < numElement; i++) {
        String key = String.valueOf(i);
        String file = html.getParameter(key);
        if (file != null) {
            al.add(file);
        }
    }
    if (lock.equals("Upload")) {
        try {
            int reply;
            ttc.connect(filename);
            CofaxToolsUtil.log(ttc.getReplyString());
            reply = ttc.getReplyCode();
            if (!FTPReply.isPositiveCompletion(reply)) {
                ttc.disconnect();
                return ("FTP server refused connection.");
            } else {
                ttc.name(name, phrase);
            }
            for (int ii = 0; ii < al.size(); ii++) {
                String fileName = (String) al.get(ii);
                String folderName = stripName(fileName);
                fileName = stripPath(fileName);
