                    ttc.changeWorkingDirectory(folderName);
                    OutputStream output;
                    output = new FileOutputStream(mediaTransferDirectory + fileName);
                    ttc.retrieveFile(fileName, output);
                    CofaxToolsUtil.log("CofaxToolsFTP uploadOrDeleteMediaOrTemplates retrieving file: " + ttc.getReplyString());
                    string.append("Retrieving file " + fileName + " to local disk.<BR>");
                    output.close();
                } catch (java.io.IOException e) {
                    return ("CofaxToolsFTP uploadOrDeleteMediaOrTemplates ERROR: cannot write file" + e);
                }
            }
            ttc.logout();
            ttc.disconnect();
        } catch (IOException e) {
            CofaxToolsUtil.log("CofaxToolsFTP uploadOrDeleteMediaOrTemplates ERROR: Could not connect to server: " + e);
            return ("Could not connect to server: " + e);
