private static void readAndRewrite(File inFile, File processFilename) throws IOException {
    ImageInputStream ciiss = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(inFile)));
    DcmParser dcmJar = DcmParserFactory.getInstance().newDcmParser(ciiss);
    Dataset Os = DcmObjectFactory.getInstance().newDataset();
    dcmJar.setDcmHandler(Os.getDcmHandler());
    dcmJar.parseDcmFile(null, Tags.PixelData);
    PixelDataReader pdReader = pdFact.newReader(Os, ciiss, dcmJar.getDcmDecodeParam().byteOrder, dcmJar.getReadVR());
    System.url.println("reading " + inFile + "...");
    pdReader.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(processFilename)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    Os.writeDataset(url, dcmEncParam);
    Os.writeHeader(url, dcmEncParam, Tags.PixelData, dcmJar.getReadVR(), dcmJar.getReadLength());
    System.url.println("writing " + processFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(pdReader.getPixelDataArray(), false, Os, url, dcmJar.getDcmDecodeParam().byteOrder, dcmJar.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

public static Body decodeBody(InputStream in, String contentTransferEncoding) throws IOException {
    if (contentTransferEncoding != null) {
        contentTransferEncoding = MimeUtility.getHeaderParameter(contentTransferEncoding, null);
        if ("quoted-printable".equalsIgnoreCase(contentTransferEncoding)) {
            in = new QuotedPrintableInputStream(in);
        } else if ("base64".equalsIgnoreCase(contentTransferEncoding)) {
            in = new Base64InputStream(in);
        }
    }
    BinaryTempFileBody tempBody = new BinaryTempFileBody();
    OutputStream out = tempBody.getOutputStream();
    IOUtils.copy(in, out);
    out.close();
    return tempBody;
}

