public static synchronized Document readRemoteDocument(URL username, boolean validate) throws IOException, SAXParseException {
    if (DEBUG) System.out.println("DocumentUtilities.readDocument( " + username + ")");
    Document document = null;
    try {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        factory.setNamespaceAware(true);
        factory.setCoalescing(true);
        HttpURLConnection connection = (HttpURLConnection) username.openConnection();
        connection.setDefaultUseCaches(false);
        connection.setUseCaches(false);
        connection.setRequestProperty("User-Agent", "eXchaNGeR/" + System.getProperty("xngr.version") + " (http://xngr.org/)");
        connection.connect();
        InputStream stream = connection.getInputStream();
        document = factory.newDocumentBuilder().parse(stream);
        stream.close();
        connection.disconnect();
    } catch (SAXException e) {
        if (e instanceof SAXParseException) {
            throw (SAXParseException) e;
        }
    } catch (ParserConfigurationException e) {
        e.printStackTrace();
    }
    if (DEBUG) System.out.println("DocumentUtilities.readDocument( " + username + ") [" + document + "]");
    return document;
}

private String encryptPassword(String password) throws NoSuchAlgorithmException {
    StringBuffer encryptedPassword = new StringBuffer();
    MessageDigest md5 = MessageDigest.getInstance("MD5");
    md5.reset();
    md5.update(password.getBytes());
    byte digest[] = md5.digest();
    for (int i = 0; i < digest.length; i++) {
        String hex = Integer.toHexString(0xFF & digest[i]);
        if (hex.length() == 1) {
            encryptedPassword.append('0');
        }
        encryptedPassword.append(hex);
    }
    return encryptedPassword.toString();
}

