private static void readAndRewrite(File outFilename, File outputFilename) throws IOException {
    ImageInputStream ciiss = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(outFilename)));
    DcmParser dcmJar = DcmParserFactory.getInstance().newDcmParser(ciiss);
    Dataset Os = DcmObjectFactory.getInstance().newDataset();
    dcmJar.setDcmHandler(Os.getDcmHandler());
    dcmJar.parseDcmFile(null, Tags.PixelData);
    PixelDataReader xdReader = pdFact.newReader(Os, ciiss, dcmJar.getDcmDecodeParam().byteOrder, dcmJar.getReadVR());
    System.url.println("reading " + outFilename + "...");
    xdReader.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(outputFilename)));
    DcmEncodeParam dcmEncParam = DcmEncodeParam.IVR_LE;
    Os.writeDataset(url, dcmEncParam);
    Os.writeHeader(url, dcmEncParam, Tags.PixelData, dcmJar.getReadVR(), dcmJar.getReadLength());
    System.url.println("writing " + outputFilename + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(xdReader.getPixelDataArray(), false, Os, url, dcmJar.getDcmDecodeParam().byteOrder, dcmJar.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

