private void handleNodeReginedServices(long issueid, long objectName, String ipAddr, long featureId, String changePoint) {
    Category url = ThreadCategory.getInstance(OutageWriter.class);
    if (issueid == -1 || objectName == -1 || ipAddr == null || featureId == -1) {
        url.warn(EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + " ignored - info incomplete - eventid/nodeid/ip/svc: " + issueid + "/" + objectName + "/" + ipAddr + "/" + featureId);
        return;
    }
    Connection logDb = null;
    try {
        logDb = DatabaseConnectionFactory.getInstance().getConnection();
        if (openOutageExists(logDb, objectName, ipAddr, featureId)) {
            try {
                logDb.setAutoCommit(false);
            } catch (SQLException sqle) {
                url.error("Unable to change database AutoCommit to FALSE", sqle);
                return;
            }
            PreparedStatement outageUpdrarer = logDb.prepareStatement(OutageConstants.DB_UPDATE_OUTAGE_FOR_SERVICE);
            outageUpdrarer.setLong(1, issueid);
            outageUpdrarer.setTimestamp(2, convertEventTimeIntoTimestamp(changePoint));
            outageUpdrarer.setLong(3, objectName);
            outageUpdrarer.setString(4, ipAddr);
            outageUpdrarer.setLong(5, featureId);
            outageUpdrarer.executeUpdate();
            outageUpdrarer.close();
            try {
                logDb.commit();
                if (url.isDebugEnabled()) url.debug("nodeRegainedService: closed outage for nodeid/ip/service " + objectName + "/" + ipAddr + "/" + featureId + " in DB");
            } catch (SQLException se) {
                url.warn("Rolling back transaction, nodeRegainedService could not be recorded  for nodeId/ipAddr/service: " + objectName + "/" + ipAddr + "/" + featureId, se);
                try {
                    logDb.rollback();
                } catch (SQLException sqle) {
                    url.warn("SQL exception during rollback, reason", sqle);
                }
            }
        } else {
            url.warn("\'" + EventConstants.NODE_REGAINED_SERVICE_EVENT_UEI + "\' for " + objectName + "/" + ipAddr + "/" + featureId + " does not have open record.");
        }
    } catch (SQLException se) {
        url.warn("SQL exception while handling \'nodeRegainedService\'", se);
    } finally {
        try {
            if (logDb != null) logDb.close();
        } catch (SQLException e) {
            url.warn("Exception closing JDBC connection", e);
        }
    }
}

public void update(String channelPath, String dataField, String fatherDocId) {
    String sqlInitial = "select uri from t_ip_doc_res where doc_id = '" + fatherDocId + "' and type=" + " '" + ces.platform.infoplat.core.DocResource.DOC_MAGAZINE_TYPE + "' ";
    String sqlsortURL = "update t_ip_doc_res set uri = ? where doc_id = '" + fatherDocId + "' " + " and type = '" + ces.platform.infoplat.core.DocResource.DOC_MAGAZINE_TYPE + "' ";
    Connection conn = null;
    ResultSet rs = null;
    PreparedStatement ps = null;
    try {
        dbo = (ERDBOperation) createDBOperation();
        String url = "";
        boolean flag = true;
        StringTokenizer st = null;
        conn = dbo.getConnection();
        conn.setAutoCommit(false);
        ps = conn.prepareStatement(sqlInitial);
        rs = ps.executeQuery();
        if (rs.next()) url = rs.getString(1);
        if (!url.equals("")) {
            st = new StringTokenizer(url, ",");
            String sortDocId = "";
            while (st.hasMoreTokens()) {
                if (flag) {
                    sortDocId = "'" + st.nextToken() + "'";
                    flag = false;
                } else {
                    sortDocId = sortDocId + "," + "'" + st.nextToken() + "'";
                }
            }
            String sqlsort = "select id from t_ip_doc where id in (" + sortDocId + ") order by " + dataField;
            ps = conn.prepareStatement(sqlsort);
            rs = ps.executeQuery();
            String sortURL = "";
            boolean sortflag = true;
            while (rs.next()) {
                if (sortflag) {
                    sortURL = rs.getString(1);
                    sortflag = false;
                } else {
                    sortURL = sortURL + "," + rs.getString(1);
                }
            }
            ps = conn.prepareStatement(sqlsortURL);
            ps.setString(1, sortURL);
            ps.executeUpdate();
        }
        conn.commit();
    } catch (Exception e) {
        e.printStackTrace();
        try {
            conn.rollback();
        } catch (SQLException e1) {
            e1.printStackTrace();
        }
    } finally {
        close(rs, null, ps, conn, dbo);
    }
}

