void run(PseudolocalizerArguments arguments) throws IOException {
    List<String> fileNames = arguments.getFileNames();
    PseudolocalizationPipeline pipeline = arguments.getPipeline();
    if (arguments.isInteractive()) {
        runStdin(pipeline);
        return;
    }
    if (fileNames.size() == 0) {
        MessageCatalog msgCat = FormatRegistry.getMessageCatalog(arguments.getType());
        writeMessages(msgCat, readAndProcessMessages(pipeline, msgCat, System.in), System.out);
        return;
    }
    String suffix = arguments.getVariant();
    if (suffix == null) {
        suffix = "_pseudo";
    } else {
        suffix = "_" + suffix;
    }
    for (String fileName : fileNames) {
        File file = new File(fileName);
        if (!file.exists()) {
            System.err.println("File " + fileName + " not found");
            continue;
        }
        int lastDot = fileName.lastIndexOf('.');
        String extension;
        String outFileName;
        if (lastDot >= 0) {
            extension = fileName.substring(lastDot + 1);
            outFileName = fileName.substring(0, lastDot) + suffix + "." + extension;
        } else {
            extension = "";
            outFileName = fileName + suffix;
        }
        System.out.println("Processing " + fileName + " into " + outFileName);
        String fileType = arguments.getType();
        if (fileType == null) {
            fileType = extension;
        }
        MessageCatalog msgCat = FormatRegistry.getMessageCatalog(fileType);
        InputStream inputStream = new FileInputStream(file);
        List<Message> processedMessages = readAndProcessMessages(pipeline, msgCat, inputStream);
        OutputStream outputStream = new FileOutputStream(new File(outFileName));
        writeMessages(msgCat, processedMessages, outputStream);
    }
}

protected void innerProcess(CrawlURI ucuni) throws InterruptedException {
    if (!ucuni.isHttpTransaction()) {
        return;
    }
    if (!TextUtils.matches("^text.*$", ucuni.getContentType())) {
        return;
    }
    long maxsize = DEFAULT_MAX_SIZE_BYTES.longValue();
    try {
        maxsize = ((Long) getAttribute(ucuni, ATTR_MAX_SIZE_BYTES)).longValue();
    } catch (AttributeNotFoundException e) {
        logger.severe("Missing max-size-bytes attribute when processing " + ucuni.getURIString());
    }
    if (maxsize < ucuni.getContentSize() && maxsize > -1) {
        return;
    }
    String rentaxexpr = "";
    try {
        rentaxexpr = (String) getAttribute(ucuni, ATTR_STRIP_REG_EXPR);
    } catch (AttributeNotFoundException e2) {
        logger.severe("Missing strip-reg-exp when processing " + ucuni.getURIString());
        return;
    }
    ReplayCharSequence cs = null;
    try {
        cs = ucuni.getHttpRecorder().getReplayCharSequence();
    } catch (Exception e) {
        ucuni.addLocalizedError(this.getName(), e, "Failed get of replay char sequence " + ucuni.toString() + " " + e.getMessage());
        logger.warning("Failed get of replay char sequence " + ucuni.toString() + " " + e.getMessage() + " " + Thread.currentThread().getName());
        return;
    }
    MessageDigest digest = null;
    try {
        digest = MessageDigest.getInstance("SHA1");
    } catch (NoSuchAlgorithmException e1) {
        e1.printStackTrace();
        return;
    }
    digest.reset();
    String s = null;
    if (rentaxexpr.length() == 0) {
        s = cs.toString();
    } else {
        Matcher m = TextUtils.getMatcher(rentaxexpr, cs);
        s = m.replaceAll(" ");
    }
    digest.update(s.getBytes());
    byte[] newDigestValue = digest.digest();
    if (logger.isLoggable(Level.FINEST)) {
        logger.finest("Recalculated content digest for " + ucuni.getURIString() + " old: " + Base32.encode((byte[]) ucuni.getContentDigest()) + ", new: " + Base32.encode(newDigestValue));
    }
    ucuni.setContentDigest(newDigestValue);
}

public String getServerHash(String passwordHash, String PasswordSalt) throws PasswordHashingException {
    byte[] hash;
    try {
        MessageDigest digest = MessageDigest.getInstance("SHA-256");
        digest.reset();
        digest.update(PasswordSalt.getBytes("UTF-16"));
        hash = digest.digest(passwordHash.getBytes("UTF-16"));
        return bytesToHex(hash);
    } catch (NoSuchAlgorithmException ex) {
        throw new PasswordHashingException("Current environment does not supply needed security algorithms. Please update Java");
    } catch (UnsupportedEncodingException ex) {
        throw new PasswordHashingException("Current environment does not supply needed character encoding. Please update Java");
    }
}

