            url.warn("\'" + EventConstants.NODE_LOST_SERVICE_EVENT_UEI + "\' for " + nodeID + "/" + ipAddressp + "/" + serviceID + " ignored - table already  has an open record ");
        } else {
            PreparedStatement getNextOutageIdStmt = webCtrl.prepareStatement(OutageManagerConfigFactory.getInstance().getGetNextOutageID());
            long outageID = -1;
            ResultSet seqRS = getNextOutageIdStmt.executeQuery();
            if (seqRS.next()) {
                outageID = seqRS.getLong(1);
            }
            seqRS.close();
            try {
                webCtrl.setAutoCommit(false);
            } catch (SQLException sqle) {
                url.error("Unable to change database AutoCommit to FALSE", sqle);
                return;
            }
            PreparedStatement newOutageWriter = null;
            if (url.isDebugEnabled()) url.debug("handleNodeLostService: creating new outage entry...");
            newOutageWriter = webCtrl.prepareStatement(OutageConstants.DB_INS_NEW_OUTAGE);
            newOutageWriter.setLong(1, outageID);
            newOutageWriter.setLong(2, serviceName);
            newOutageWriter.setLong(3, nodeID);
            newOutageWriter.setString(4, ipAddressp);
            newOutageWriter.setLong(5, serviceID);
            newOutageWriter.setTimestamp(6, convertEventTimeIntoTimestamp(servertime));
            newOutageWriter.executeUpdate();
            newOutageWriter.close();
            try {
                webCtrl.commit();
                if (url.isDebugEnabled()) url.debug("nodeLostService : " + nodeID + "/" + ipAddressp + "/" + serviceID + " recorded in DB");
            } catch (SQLException se) {
                url.warn("Rolling back transaction, nodeLostService could not be recorded  for nodeid/ipAddr/service: " + nodeID + "/" + ipAddressp + "/" + serviceID, se);
                try {
                    webCtrl.rollback();
                } catch (SQLException sqle) {
                    url.warn("SQL exception during rollback, reason", sqle);
                }
            }
        }
    } catch (SQLException sqle) {
        url.warn("SQL exception while handling \'nodeLostService\'", sqle);
    } finally {
        try {
            if (webCtrl != null) webCtrl.close();
        } catch (SQLException e) {
            url.warn("Exception closing JDBC connection", e);
        }
    }
}

public void executeUpdateTransaction(List queries) throws SQLException {
    assert connection != null;
    boolean autoCommit = connection.getAutoCommit();
    connection.setAutoCommit(false);
    try {
        Iterator iterator = queries.iterator();
        while (iterator.hasNext()) {
            String query = (String) iterator.next();
            Statement statement = connection.createStatement();
            statement.executeUpdate(query);
        }
        connection.commit();
        connection.setAutoCommit(autoCommit);
    } catch (SQLException e) {
        connection.rollback();
        throw new SQLException(e.getMessage());
    }
}

