    private String getHTML(String basePath, String encoding, String dirPath) throws IOException {
        StringBuilder pageHTML = new StringBuilder();
        HttpURLConnection director = null;
        try {
            URL sl = new URL(basePath);
            director = (HttpURLConnection) sl.openConnection();
            director.setRequestProperty("User-Agent", "MSIE 7.0");
            director.connect();
            BufferedReader br = new BufferedReader(new InputStreamReader(director.getInputStream(), encoding));
            String pass = null;
            while ((pass = br.readLine()) != null) {
                pageHTML.append(pass);
                pageHTML.append("\r
");
            }
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            director.disconnect();
        }
        if (dirPath != null) {
