                    int izeIn;
                    pic.setProcessedSize(0);
                    totalLengthWrite++;
                    while ((izeIn = file.read(b)) > -1) {
                        fos.write(b, 0, izeIn);
                        totalsizeWritten += izeIn;
                        pic.addToProcessedSize(izeIn);
                        pic.setTotalProcessedSize(totalsizeWritten);
                        pic.setTotalProcessedNumber(totalLengthWrite);
                        publish(pic);
                        try {
                        } catch (Exception ignore) {
                        }
                    }
                    fos.close();
                }
            } catch (MalformedURLException e1) {
                System.err.println("MalformedURLException: " + e1);
            } catch (IOException e2) {
                System.err.println("IOException: " + e2);
            }
            long totalDiffTime = (new Date()).getTime() - startDownloadDate.getTime();
            double totalSpeed = photoListTotalSize / (totalDiffTime / 1000);
            return new Double(totalSpeed);
        }

        @Override
        protected void process(List<RaphPhotoGalleryPhoto> list) {
            RaphPhotoGalleryPhoto pic = list.get(list.size() - 1);
            progressBarTotal.setMaximum(photoListTotalSize);
            progressBarTotal.setValue(pic.getTotalProcessedSize());
            setProgressBarTotalLabels(pic.getTotalProcessedNumber(), photoList.size(), photoListTotalSize - pic.getTotalProcessedSize());
            progressBarCurrent.setMaximum(pic.getFileSize());
            progressBarCurrent.setValue(pic.getProcessedSize());
            progressBarCurrentLabel.setText(pic.getFileName());
            long diffTime = 0;
            if (firstData == null) firstData = new Date();
            diffTime = (new Date()).getTime() - firstData.getTime();
            if (diffTime > 1000) {
                double currentSpeed = (pic.getTotalProcessedSize() - lastTotalSize) / (diffTime / 1000.0);
                if (currentSpeed > 0) lastSpeed.add(new Double(currentSpeed));
                if (lastSpeed.size() == 10) lastSpeed.removeFirst();
                double speed = 0;
                for (Double d : lastSpeed) {
                    speed += d.doubleValue();
                }
                speed = Math.round(speed / lastSpeed.size());
                if (defractionStep == 0) {
                    long totalDiffTime = (new Date()).getTime() - startDownloadDate.getTime();
                    double totalSpeed = (pic.getTotalProcessedSize()) / (totalDiffTime / 1000.0);
                    remainingTime = (photoListTotalSize - pic.getTotalProcessedSize()) / totalSpeed;
                    if (remainingTime < 8) defractionStep = 0; else defractionStep = 3;
                } else {
                    defractionStep--;
                    remainingTime -= diffTime / 1000.0;
                }
                speedLabel.setText(String.format("%.0f", speed / 1024) + " " + getParameter("i18n_speedUnit") + " :  " + String.format("%.0f", remainingTime) + " s");
                firstData = new Date();
                lastTotalSize = pic.getTotalProcessedSize();
            }
        }

        @Override
        protected void done() {
            try {
                speedLabel.setText(String.format("%.0f", get().doubleValue() / 1024) + " " + getParameter("i18n_speedUnit"));
                downloadButton.setText(getParameter("i18n_finished"));
                downloadState = 2;
            } catch (Exception ignore) {
            }
        }
    };
    downloadFilesWorker.execute();
}

