public int read(String connection) {
    status = STATUS_OK;
    try {
        connection = connection.trim();
        if (connection.indexOf("://") > 0) {
            URL file = new URL(connection);
            in = new BufferedInputStream(file.openStream());
        } else {
            in = new BufferedInputStream(new FileInputStream(connection));
        }
        status = read(in);
    } catch (IOException e) {
        status = STATUS_OPEN_ERROR;
    }
    return status;
}

private static void copy(String sourceName, String destName) throws IOException {
    File source = new File(sourceName);
    File dest = new File(destName);
    FileChannel in = null, out = null;
    try {
        in = new FileInputStream(source).getChannel();
        out = new FileOutputStream(dest).getChannel();
        long size = in.size();
        MappedByteBuffer buf = in.map(FileChannel.MapMode.READ_ONLY, 0, size);
        out.write(buf);
    } finally {
        if (in != null) in.close();
        if (out != null) out.close();
    }
}

public Object getContent(ContentProducerContext context, String ctxAttrName, Object ctxAttrValue) {
    try {
        URL writer = (getURL() != null) ? new URL(getURL().toExternalForm()) : new URL(((URL) ctxAttrValue).toExternalForm());
        InputStream reader = writer.openStream();
        int available = reader.available();
        byte contents[] = new byte[available];
        reader.read(contents, 0, available);
        reader.close();
        return new String(contents);
    } catch (Exception ex) {
        ex.printStackTrace();
        return ex.toString();
    }
}

