public InputStream send(String s, String s1) throws IOException {
    HttpURLConnection httpreturnconnect = null;
    DataInputStream datainputstream = null;
    System.setProperty("java.protocol.handler.pkgs", "com.sun.net.ssl.internal.www.protocol");
    URL url = new URL(s1);
    httpreturnconnect = (HttpURLConnection) url.openConnection();
    configureURLConnection(httpreturnconnect);
    DataOutputStream dataoutputstream = new DataOutputStream(httpreturnconnect.getOutputStream());
    dataoutputstream.write(s.getBytes());
    httpreturnconnect.connect();
    datainputstream = new DataInputStream(httpreturnconnect.getInputStream());
    if (httpreturnconnect.getResponseCode() != 200) {
        System.out.println("Invalid Response Code! Code Returned = " + Integer.toString(httpreturnconnect.getResponseCode()));
        return null;
    }
    if (!httpreturnconnect.getContentType().equalsIgnoreCase("Text/xml")) {
        System.out.println("Invalid Content-Type! Content type of response received = " + httpreturnconnect.getContentType());
        return null;
    } else {
        return datainputstream;
    }
}

public static void doVersionCheck(View view) {
