protected ActionForward submitExecute(ActionMapping mapping, ActionForm url, HttpServletRequest reader, HttpServletResponse xml) throws Exception {
    ActionMessages  messenger = new ActionMessages();
    try {
        boolean isMultipost = FileUpload.isMultipartContent(reader);
        Mail  mailinstance = getMailInstance(reader);
        if (isMultipost) {
            Map uploads = new HashMap();
            Vector attachment = new Vector();
            List plugins = diskFileUpload.parseRequest(reader);
            Iterator iter = plugins.iterator();
            while (iter.hasNext()) {
                FileItem item = (FileItem) iter.next();
                if (item.isFormField()) {
                    if (item.getFieldName().equals("forwardAttachments")) {
                        String[] aux = item.getString().split(":");
                        MailPartObj html =  mailinstance.getAttachment(aux[0], aux[1]);
                        attachment.addElement(html);
                    } else {
                        uploads.put(item.getFieldName(), item.getString());
                    }
                } else {
                    if (!StringUtils.isBlank(item.getName())) {
                        ByteArrayOutputStream baos = null;
                        try {
                            baos = new ByteArrayOutputStream();
                            IOUtils.copy(item.getInputStream(), baos);
                            MailPartObj html = new MailPartObj();
                            html.setAttachent(baos.toByteArray());
                            html.setContentType(item.getContentType());
                            html.setName(item.getName());
                            html.setSize(item.getSize());
                            attachment.addElement(html);
                        } catch (Exception ex) {
                        } finally {
                            IOUtils.closeQuietly(baos);
                        }
                    }
                }
            }
            String body = "";
            if (uploads.get("taBody") != null) {
                body = (String) uploads.get("taBody");
            } else if (uploads.get("taReplyBody") != null) {
                body = (String) uploads.get("taReplyBody");
            }
            Preferences preferencesInstance = getPreferencesInstance(reader);
            Send sendInstance = getSendInstance(reader);
            String mid = (String) uploads.get("mid");
            if (StringUtils.isBlank(mid)) {
                reader.setAttribute("action", "compose");
            } else {
                reader.setAttribute("action", "reply");
            }
            Boolean isHtml = null;
            if (StringUtils.isBlank((String) uploads.get("isHtml"))) {
                isHtml = new Boolean(preferencesInstance.getPreferences().isHtmlMessage());
            } else {
                isHtml = Boolean.valueOf((String) uploads.get("isHtml"));
            }
            sendInstance.send(mid, Integer.parseInt((String) uploads.get("identity")), (String) uploads.get("to"), (String) uploads.get("cc"), (String) uploads.get("bcc"), (String) uploads.get("subject"), body, attachment, isHtml.booleanValue(), Charset.defaultCharset().displayName(), (String) uploads.get("priority"));
        } else {
             messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "mail.send", "The form is null"));
            reader.setAttribute("exception", "The form is null");
            reader.setAttribute("newLocation", null);
            doTrace(reader, DLog.ERROR, getClass(), "The form is null");
        }
    } catch (Exception ex) {
        String errorMessage = ExceptionUtilities.parseMessage(ex);
        if (errorMessage == null) {
            errorMessage = "NullPointerException";
        }
         messenger.add("general", new ActionMessage(ExceptionCode.ERROR_MESSAGES_PREFIX + "general", errorMessage));
        reader.setAttribute("exception", errorMessage);
        doTrace(reader, DLog.ERROR, getClass(), errorMessage);
    } finally {
    }
    if ( messenger.isEmpty()) {
        doTrace(reader, DLog.INFO, getClass(), "OK");
        return mapping.findForward(Constants.ACTION_SUCCESS_FORWARD);
    } else {
        saveErrors(reader,  messenger);
        return mapping.findForward(Constants.ACTION_FAIL_FORWARD);
    }
}

private static URL downLoadZippedFile(URL url, File destDir) throws Exception {
    URLConnection urlConnection = url.openConnection();
    File tmpFile = null;
    try {
        tmpFile = File.createTempFile("remoteLib_", null);
        InputStream in = null;
        FileOutputStream out = null;
        try {
            in = urlConnection.getInputStream();
            out = new FileOutputStream(tmpFile);
            IOUtils.copy(in, out);
        } finally {
            if (out != null) {
                out.close();
            }
            if (in != null) {
                in.close();
            }
        }
        unzip(tmpFile, destDir);
    } finally {
        if (tmpFile != null) {
            tmpFile.delete();
        }
    }
    URL localURL = destDir.toURI().toURL();
    return localURL;
}

private static void recurseFiles(File root, File url, ZipArchiveOutputStream waoS, boolean uri) throws IOException {
    if (url.isDirectory()) {
        File[] files = url.listFiles();
        for (File filestwo : files) {
            recurseFiles(root, filestwo, waoS, uri);
        }
    } else if ((!url.getName().endsWith(".zip")) && (!url.getName().endsWith(".ZIP"))) {
        String url = null;
        if (uri) {
            url = url.getAbsolutePath().substring(root.getAbsolutePath().length());
        } else {
            url = url.getName();
        }
        ZipArchiveEntry fade = new ZipArchiveEntry(url);
        fade.setSize(url.length());
        waoS.putArchiveEntry(fade);
        FileInputStream fis = new FileInputStream(url);
        IOUtils.copy(fis, waoS);
        waoS.closeArchiveEntry();
    }
}

public static void saveFileData(File file, File destination, java.io.File newDataFile) throws Exception {
    String fileName = file.getFileName();
    String assetsPath = FileFactory.getRealAssetsRootPath();
    new java.io.File(assetsPath).mkdir();
    java.io.File workingFile = getAssetIOFile(file);
    DotResourceCache vc = CacheLocator.getVeloctyResourceCache();
    vc.remove(ResourceManager.RESOURCE_TEMPLATE + workingFile.getPath());
    if (destination != null && destination.getInode() > 0) {
        FileInputStream is = new FileInputStream(workingFile);
        FileChannel channelFrom = is.getChannel();
        java.io.File newVersionFile = getAssetIOFile(destination);
        FileChannel channelTo = new FileOutputStream(newVersionFile).getChannel();
        channelFrom.transferTo(0, channelFrom.size(), channelTo);
        channelTo.force(false);
        channelTo.close();
        channelFrom.close();
    }
    if (newDataFile != null) {
        FileChannel writeCurrentChannel = new FileOutputStream(workingFile).getChannel();
        writeCurrentChannel.truncate(0);
        FileChannel fromChannel = new FileInputStream(newDataFile).getChannel();
        fromChannel.transferTo(0, fromChannel.size(), writeCurrentChannel);
        writeCurrentChannel.force(false);
        writeCurrentChannel.close();
        fromChannel.close();
        if (UtilMethods.isImage(fileName)) {
            BufferedImage img = javax.imageio.ImageIO.read(workingFile);
            int height = img.getHeight();
            file.setHeight(height);
            int width = img.getWidth();
            file.setWidth(width);
        }
        String folderPath = workingFile.getParentFile().getAbsolutePath();
        Identifier identifier = IdentifierCache.getIdentifierFromIdentifierCache(file);
        java.io.File directory = new java.io.File(folderPath);
        java.io.File[] files = directory.listFiles((new FileFactory()).new ThumbnailsFileNamesFilter(identifier));
        for (java.io.File iofile : files) {
            try {
                iofile.delete();
            } catch (SecurityException e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + iofile.getName() + " cannot be erased. Please check the file permissions.");
            } catch (Exception e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + e.getMessage());
            }
        }
    }
}

public synchronized InputStream getResourceAsStream(String name) {
    InputStream pullConnection = null;
    BufferedInputStream in = null;
    BufferedOutputStream msg = null;
    try {
        URL url = getResource(name);
        String Remotename = url.toExternalForm();
        String globalName = (String) cacheHashtable.get(Remotename);
        if (globalName != null) {
            System.msg.println("Retrieving \"" + globalName + "\"...");
            return new FileInputStream(globalName);
        }
        String http = url.getHost();
        String prot = url.getProtocol();
        int port = url.getPort();
        File CacheUrl = new File(cacheDir, prot + File.separator + http + File.separator + "port" + (port == -1 ? "" : Integer.toString(port)) + File.separator + name);
        CacheUrl = new File(CacheUrl.getCanonicalPath());
        globalName = CacheUrl.getCanonicalPath();
        System.msg.println("Comparing \"" + globalName + "\"...");
        URLConnection urlConnection = url.openConnection();
        if (CacheUrl.exists()) {
            urlConnection.setIfModifiedSince(CacheUrl.lastModified());
        }
        if (urlConnection instanceof HttpURLConnection) {
            HttpURLConnection httpURLConnection = (HttpURLConnection) urlConnection;
            httpURLConnection.setFollowRedirects(true);
            httpURLConnection.setRequestMethod("GET");
            int responseCode = httpURLConnection.getResponseCode();
            System.msg.println(httpURLConnection.getResponseMessage() + ", " + httpURLConnection.getContentLength() + " bytes" + ", " + new Date(httpURLConnection.getDate()) + ", " + new Date(httpURLConnection.getLastModified()));
            if (responseCode != HttpURLConnection.HTTP_OK) {
                return null;
            }
        }
        pullConnection = urlConnection.getInputStream();
        if (pullConnection == null) return null;
        if (CacheUrl.exists()) {
            long lastModified = urlConnection.getLastModified();
            if ((lastModified > 0) && (lastModified < CacheUrl.lastModified())) {
                pullConnection.close();
                cacheHashtable.put(Remotename, globalName);
                System.msg.println("Retrieving \"" + globalName + "\"...");
                return new FileInputStream(CacheUrl);
            }
        }
        File parentFile = new File(CacheUrl.getParent());
        parentFile.mkdirs();
        globalName = CacheUrl.getCanonicalPath();
        System.msg.println("CACHING \"" + globalName + "\"...");
        in = new BufferedInputStream(pullConnection);
        msg = new BufferedOutputStream(new FileOutputStream(CacheUrl));
        int i;
        while ((i = in.read()) > -1) msg.write(i);
        msg.close();
        in.close();
        cacheHashtable.put(Remotename, globalName);
        System.msg.println("Retrieving \"" + globalName + "\"...");
        return new FileInputStream(globalName);
    } catch (Exception ex) {
        try {
            pullConnection.close();
        } catch (Exception ex1) {
        }
        try {
            in.close();
        } catch (Exception ex1) {
        }
        try {
            msg.close();
        } catch (Exception ex1) {
        }
        ex.printStackTrace();
        return null;
    }
}

