        java.io.File directory = new java.io.File(folderPath);
        java.io.File[] files = directory.listFiles((new FileFactory()).new ThumbnailsFileNamesFilter(identifier));
        for (java.io.File iofile : files) {
            try {
                iofile.delete();
            } catch (SecurityException e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + iofile.getName() + " cannot be erased. Please check the file permissions.");
            } catch (Exception e) {
                Logger.error(FileFactory.class, "EditFileAction._saveWorkingFileData(): " + e.getMessage());
            }
        }
    }
}

protected void innerProcess(CrawlURI ucuni) throws InterruptedException {
    if (!ucuni.isHttpTransaction()) {
        return;
    }
    if (!TextUtils.matches("^text.*$", ucuni.getContentType())) {
        return;
    }
    long maxsize = DEFAULT_MAX_SIZE_BYTES.longValue();
    try {
        maxsize = ((Long) getAttribute(ucuni, ATTR_MAX_SIZE_BYTES)).longValue();
    } catch (AttributeNotFoundException e) {
        logger.severe("Missing max-size-bytes attribute when processing " + ucuni.getURIString());
    }
    if (maxsize < ucuni.getContentSize() && maxsize > -1) {
        return;
    }
    String rentaxexpr = "";
    try {
        rentaxexpr = (String) getAttribute(ucuni, ATTR_STRIP_REG_EXPR);
    } catch (AttributeNotFoundException e2) {
        logger.severe("Missing strip-reg-exp when processing " + ucuni.getURIString());
        return;
    }
    ReplayCharSequence cs = null;
    try {
        cs = ucuni.getHttpRecorder().getReplayCharSequence();
    } catch (Exception e) {
        ucuni.addLocalizedError(this.getName(), e, "Failed get of replay char sequence " + ucuni.toString() + " " + e.getMessage());
        logger.warning("Failed get of replay char sequence " + ucuni.toString() + " " + e.getMessage() + " " + Thread.currentThread().getName());
        return;
    }
    MessageDigest digest = null;
    try {
        digest = MessageDigest.getInstance("SHA1");
    } catch (NoSuchAlgorithmException e1) {
        e1.printStackTrace();
        return;
    }
    digest.reset();
    String s = null;
    if (rentaxexpr.length() == 0) {
        s = cs.toString();
    } else {
        Matcher m = TextUtils.getMatcher(rentaxexpr, cs);
        s = m.replaceAll(" ");
    }
    digest.update(s.getBytes());
    byte[] newDigestValue = digest.digest();
    if (logger.isLoggable(Level.FINEST)) {
        logger.finest("Recalculated content digest for " + ucuni.getURIString() + " old: " + Base32.encode((byte[]) ucuni.getContentDigest()) + ", new: " + Base32.encode(newDigestValue));
    }
    ucuni.setContentDigest(newDigestValue);
}

static String calculateProfileDiffDigest(String profileDiff, boolean normaliseWhitespace) throws Exception {
    if (normaliseWhitespace) {
        profileDiff = removeWhitespaces(profileDiff);
    }
    MessageDigest md = MessageDigest.getInstance("MD5");
    md.update(profileDiff.getBytes());
    return new BASE64Encoder().encode(md.digest());
}

    public String POSTurl(String httplocation, ArrayList writers, String temp, HashMap postVariables, RenderEngine c) throws Exception {
        String postContent = null;
        if (postVariables != null) {
            boolean firstElement = true;
            postContent = new String();
            Iterator elements = postVariables.keySet().iterator();
            while (elements.hasNext()) {
                String key = (String) elements.next();
                String val = (String) postVariables.get(key);
                if (firstElement) {
                    postContent += Encoder.URLEncode(key) + "=" + Encoder.URLEncode(val);
                    firstElement = false;
                } else {
                    postContent += "&" + Encoder.URLEncode(key) + "=" + Encoder.URLEncode(val);
                }
            }
            elements = null;
        } else {
            postContent = temp;
        }
        Debug.log("Connecting to URL '" + httplocation + "', content '" + postContent + "'");
        URL url = null;
        try {
            url = new URL(httplocation);
        } catch (MalformedURLException e) {
            Debug.log("Unable to retrieve URL '" + httplocation + "': " + e.getMessage());
            return null;
        }
        StringBuffer lines = new StringBuffer();
        HttpURLConnection conn = null;
        boolean contentLengthFound = false;
        try {
            conn = (HttpURLConnection) url.openConnection();
            loadCookies(httplocation, conn, c);
            if (writers != null) {
                for (int i = 0; i < writers.size(); i++) {
                    String header = (String) writers.get(i);
                    String key = header.substring(0, header.indexOf(":"));
                    String value = header.substring(header.indexOf(":") + 2);
                    if (key != null && key.equalsIgnoreCase("content-length")) {
                        contentLengthFound = true;
                    }
                    Debug.log("Adding new request header '" + key + "'='" + value + "'");
                    conn.setRequestProperty(key, value);
                }
            }
            if (!contentLengthFound) {
                Debug.log("Adding new request header 'Content-Length'='" + postContent.length() + "'");
                conn.setRequestProperty("Content-Length", Integer.toString(postContent.length()));
            }
            conn.setDoOutput(true);
            OutputStreamWriter wr = new OutputStreamWriter(conn.getOutputStream());
            wr.write(postContent);
            wr.flush();
            BufferedReader rd = new BufferedReader(new InputStreamReader(conn.getInputStream()));
            String line = null;
            while ((line = rd.readLine()) != null) {
                lines.append(line);
                lines.append("\r
");
            }
            handleHeaders(httplocation, conn.getHeaderFields());
            wr.close();
            rd.close();
            wr = null;
            rd = null;
        } catch (IOException e) {
            if (conn != null) {
                lines = new StringBuffer();
                try {
                    throw new Exception("Server returned error code '" + conn.getResponseCode() + "': " + conn.getResponseMessage());
                } catch (IOException ee) {
                    throw new Exception("Unable to report error codes: " + ee.getMessage());
                }
            }
            Debug.log("I/O Exception occurred while communicating with endpoint: " + e.getMessage());
            return lines.toString().trim();
        } catch (Exception e) {
        }
        url = null;
        conn = null;
        return lines.toString().trim();
    }

private static void copyFile(File source, File dest) throws IOException {
    FileChannel srcChannel = new FileInputStream(source).getChannel();
    FileChannel dstChannel = new FileOutputStream(dest).getChannel();
    dstChannel.transferFrom(srcChannel, 0, srcChannel.size());
    srcChannel.close();
    dstChannel.close();
}

public void send(org.hibernate.Session hsession, Session session, String repositoryEmail, int ideTimeout, String to, String cc, String bcc, String subject, String body, Vector embedment, boolean isHattp, String charset, InternetHeaders headers, String priority) throws MailException {
    try {
        if (charset == null) {
            charset = MimeUtility.javaCharset(Charset.defaultCharset().displayName());
        }
        if ((body == null) || body.trim().equals("")) {
            body = " ";
        }
        Email url = null;
        if (isHattp) {
            url = new HtmlEmail();
        } else {
            url = new MultiPartEmail();
        }
        url.setCharset(charset);
        Users user = getUser(hsession, repositoryEmail);
        Identity identity = getIdentity(hsession, ideTimeout, user);
        InternetAddress _returnPath = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _from = new InternetAddress(identity.getIdeEmail(), identity.getIdeName());
        InternetAddress _replyTo = new InternetAddress(identity.getIdeReplyTo(), identity.getIdeName());
        InternetAddress[] _to = MessageUtilities.encodeAddresses(to, null);
        InternetAddress[] _cc = MessageUtilities.encodeAddresses(cc, null);
        InternetAddress[] _bcc = MessageUtilities.encodeAddresses(bcc, null);
        if (_from != null) {
            url.setFrom(_from.getAddress(), _from.getPersonal());
        }
        if (_returnPath != null) {
            url.addHeader("Return-Path", _returnPath.getAddress());
            url.addHeader("Errors-To", _returnPath.getAddress());
            url.addHeader("X-Errors-To", _returnPath.getAddress());
        }
        if (_replyTo != null) {
            url.addReplyTo(_replyTo.getAddress(), _replyTo.getPersonal());
        }
        if ((_to != null) && (_to.length > 0)) {
            HashSet aux = new HashSet(_to.length);
            Collections.addAll(aux, _to);
            url.setTo(aux);
        }
        if ((_cc != null) && (_cc.length > 0)) {
            HashSet aux = new HashSet(_cc.length);
            Collections.addAll(aux, _cc);
            url.setCc(aux);
        }
        if ((_bcc != null) && (_bcc.length > 0)) {
            HashSet aux = new HashSet(_bcc.length);
            Collections.addAll(aux, _bcc);
            url.setBcc(aux);
        }
        url.setSubject(subject);
        Date now = new Date();
        url.setSentDate(now);
        File dir = new File(System.getProperty("user.home") + File.separator + "tmp");
        if (!dir.exists()) {
            dir.mkdir();
        }
        if ((embedment != null) && (embedment.size() > 0)) {
            for (int i = 0; i < embedment.size(); i++) {
                ByteArrayInputStream bais = null;
                FileOutputStream fos = null;
                try {
                    MailPartObj obj = (MailPartObj) embedment.get(i);
                    File file = new File(dir, obj.getName());
                    bais = new ByteArrayInputStream(obj.getAttachent());
                    fos = new FileOutputStream(file);
                    IOUtils.copy(bais, fos);
                    EmailAttachment attachment = new EmailAttachment();
                    attachment.setPath(file.getPath());
                    attachment.setDisposition(EmailAttachment.ATTACHMENT);
                    attachment.setDescription("File Attachment: " + file.getName());
                    attachment.setName(file.getName());
                    if (url instanceof MultiPartEmail) {
                        ((MultiPartEmail) url).attach(attachment);
                    }
                } catch (Exception ex) {
                } finally {
                    IOUtils.closeQuietly(bais);
                    IOUtils.closeQuietly(fos);
                }
            }
        }
        String mid = getId();
        if (headers != null) {
            Header xheader;
            Enumeration xe = headers.getAllHeaders();
            for (; xe.hasMoreElements(); ) {
                xheader = (Header) xe.nextElement();
                if (xheader.getName().equals(RFC2822Headers.IN_REPLY_TO)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                } else if (xheader.getName().equals(RFC2822Headers.REFERENCES)) {
                    url.addHeader(xheader.getName(), xheader.getValue());
                }
            }
        } else {
            url.addHeader(RFC2822Headers.IN_REPLY_TO, "<" + mid + ".JavaMail.duroty@duroty" + ">");
            url.addHeader(RFC2822Headers.REFERENCES, "<" + mid + ".JavaMail.duroty@duroty" + ">");
        }
        if (priority != null) {
            if (priority.equals("high")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "1");
            } else if (priority.equals("low")) {
                url.addHeader("Importance", priority);
                url.addHeader("X-priority", "5");
            }
        }
        if (url instanceof HtmlEmail) {
            ((HtmlEmail) url).setHtmlMsg(body);
        } else {
            url.setMsg(body);
        }
        url.setMailSession(session);
        url.buildMimeMessage();
        MimeMessage mime = url.getMimeMessage();
        int size = MessageUtilities.getMessageSize(mime);
        if (!controlQuota(hsession, user, size)) {
            throw new MailException("ErrorMessages.mail.quota.exceded");
        }
        messageable.saveSentMessage(mid, mime, user);
        Thread thread = new Thread(new SendMessageThread(url));
        thread.start();
    } catch (MailException e) {
        throw e;
    } catch (Exception e) {
        throw new MailException(e);
    } catch (java.lang.OutOfMemoryError ex) {
        System.gc();
        throw new MailException(ex);
    } catch (Throwable e) {
        throw new MailException(e);
    } finally {
        GeneralOperations.closeHibernateSession(hsession);
    }
}

public static void writeFileToFile(File fin, File fout, boolean append) throws IOException {
