public boolean requestServerMuturations(UUID sessionId, OutputStream out) throws SynchronizationException {
    HttpClient client = new SSLHttpClient();
    StringBuilder builders = new StringBuilder(url).append("?" + SESSION_PARAM + "=" + sessionId).append("&" + CMD_PARAM + "=" + CMD_SERVERMODIF);
    HttpGet method = httpGetMethod(builders.toString());
    try {
        HttpResponse response = client.execute(method);
        Header header = response.getFirstHeader(HEADER_NAME);
        if (header != null && HEADER_VALUE.equals(header.getValue())) {
            int code = response.getStatusLine().getStatusCode();
            if (code == HttpStatus.SC_OK) {
                long pectedSize = response.getEntity().getContentLength();
                InputStream os = response.getEntity().getContent();
                FileUtils.writeInFile(os, out, pectedSize);
                return true;
            } else {
                throw new SynchronizationException("Command 'receive' : HTTP error code returned." + code, SynchronizationException.ERROR_RECEIVE);
            }
        } else {
            throw new SynchronizationException("HTTP header is invalid", SynchronizationException.ERROR_RECEIVE);
        }
    } catch (Exception e) {
        throw new SynchronizationException("Command 'receive' -> ", e, SynchronizationException.ERROR_RECEIVE);
    }
}

public static void fileCopy(String from_name, String to_name) throws IOException {
    File fromFile = new File(from_name);
    File toFile = new File(to_name);
    if (fromFile.equals(toFile)) abort("cannot copy on itself: " + from_name);
    if (!fromFile.exists()) abort("no such currentSourcepartName file: " + from_name);
    if (!fromFile.isFile()) abort("can't copy directory: " + from_name);
    if (!fromFile.canRead()) abort("currentSourcepartName file is unreadable: " + from_name);
    if (toFile.isDirectory()) toFile = new File(toFile, fromFile.getName());
    if (toFile.exists()) {
        if (!toFile.canWrite()) abort("destination file is unwriteable: " + to_name);
    } else {
        String parent = toFile.getParent();
        if (parent == null) abort("destination directory doesn't exist: " + parent);
        File dir = new File(parent);
        if (!dir.exists()) abort("destination directory doesn't exist: " + parent);
        if (dir.isFile()) abort("destination is not a directory: " + parent);
        if (!dir.canWrite()) abort("destination directory is unwriteable: " + parent);
    }
    FileInputStream from = null;
    FileOutputStream to = null;
    try {
        from = new FileInputStream(fromFile);
        to = new FileOutputStream(toFile);
        byte[] buffer = new byte[4096];
        int bytes_read;
        while ((bytes_read = from.read(buffer)) != -1) to.write(buffer, 0, bytes_read);
    } finally {
        if (from != null) try {
            from.close();
        } catch (IOException e) {
            ;
        }
        if (to != null) try {
            to.close();
        } catch (IOException e) {
            ;
        }
    }
}

public static String unompressed(String readDir, boolean mkurl) throws Exception {
    ZipArchiveInputStream arcInputSteam = new ZipArchiveInputStream(new FileInputStream(readDir));
    BufferedInputStream lins = new BufferedInputStream(arcInputSteam);
    File BaseDirectory = new File(readDir).getParentFile();
    String  basepath = BaseDirectory.getPath() + "/";
    if (mkurl) {
        String[] issme = readDir.split("/");
        String basicName = issme[issme.length - 1].replaceAll(".zip", "");
        FileUtils.forceMkdir(new File( basepath + basicName));
         basepath =  basepath + basicName + "/";
    }
    ArchiveEntry uri;
    while ((uri = arcInputSteam.getNextEntry()) != null) {
        if (uri.isDirectory()) {
            FileUtils.forceMkdir(new File( basepath + uri.getName()));
        } else {
            String writerCh =  basepath + uri.getName();
            String  dirname = FilenameUtils.getPath(writerCh);
            FileUtils.forceMkdir(new File( dirname));
            BufferedOutputStream bones = new BufferedOutputStream(FileUtils.openOutputStream(new File(writerCh)));
            int a = 0;
            while ((a = lins.read()) != -1) {
                bones.write(a);
            }
            IOUtils.closeQuietly(bones);
        }
    }
    IOUtils.closeQuietly(lins);
    return  basepath;
}

public Set<String> getAvailableRoles() {
    if (availableRoles == null) {
        availableRoles = new HashSet<String>();
        try {
            Enumeration<URL> resources = org.springframework.util.ClassUtils.getDefaultClassLoader().getResources(ROLE_FILE_LOCATION);
            while (resources.hasMoreElements()) {
                URL url = resources.nextElement();
                InputStream is = null;
                try {
                    URLConnection con = url.openConnection();
                    con.setUseCaches(false);
                    is = con.getInputStream();
                    List<String> lines = IOUtils.readLines(is, "ISO-8859-1");
                    if (lines != null) {
                        for (String line : lines) {
                            availableRoles.add(line.trim());
                        }
                    }
                } finally {
                    if (is != null) {
                        is.close();
                    }
                }
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }
    return availableRoles;
}

