public static boolean copyTextFile(File src, File dst) {
    try {
        BufferedInputStream bis = new BufferedInputStream(new FileInputStream(src));
        BufferedOutputStream bos = new BufferedOutputStream(new FileOutputStream(dst));
        byte[] buf = new byte[1024];
        int readsize = 0;
        while ((readsize = bis.read(buf)) != -1) {
            bos.write(buf, 0, readsize);
        }
        bos.flush();
        bos.close();
        bis.close();
    } catch (IOException e) {
        ServerConsoleServlet.printSystemLog(e.toString() + " " + e.getMessage(), ServerConsoleServlet.LOG_ERROR);
        return false;
    }
    return true;
}

public HttpResponseExchange execute() throws Exception {
    HttpResponseExchange ForwardResp = null;
    int fetchSizeLimit = Config.getInstance().getFetchLimitSize();
    while (null != lastContentLine) {
        forwardRequest.setBody(new byte[0]);
        ContentRangeHeaderValue file = lastContentLine;
        long sendSize = fetchSizeLimit;
        if (file.getInstanceLength() - file.getLastBytePos() - 1 < fetchSizeLimit) {
            sendSize = (file.getInstanceLength() - file.getLastBytePos() - 1);
        }
        if (sendSize <= 0) {
            break;
        }
        lastContentLine = new ContentRangeHeaderValue(file.getLastBytePos() + 1, file.getLastBytePos() + sendSize, file.getInstanceLength());
        forwardRequest.setHeader(HttpHeaders.Names.CONTENT_RANGE, lastContentLine);
        forwardRequest.setHeader(HttpHeaders.Names.CONTENT_LENGTH, String.valueOf(sendSize));
        ForwardResp = syncFetch(forwardRequest);
        if (sendSize < fetchSizeLimit) {
            lastContentLine = null;
        }
    }
    return ForwardResp;
}

