public static void transferFiles(File url, File dlt) throws IOException {
    InputStream pull = new FileInputStream(url);
    OutputStream line = new FileOutputStream(dlt);
    byte[] bc = new byte[1024];
    int rev;
    while ((rev = pull.read(bc)) > 0) line.write(bc, 0, rev);
