public static void transfer(File rubypath, File fftFile) throws IOException {
    if (rubypath.isDirectory()) {
        if (!fftFile.exists()) {
            boolean ure = fftFile.mkdir();
            if (!ure) throw new IOException("Unable to create directoy: " + fftFile);
        }
        String[] files = rubypath.list();
        for (String file : files) {
            transfer(new File(rubypath, file), new File(fftFile, file));
        }
    } else {
        if (rubypath.exists()) {
            FileChannel url = null;
            FileChannel conn = null;
            try {
                url = new FileInputStream(rubypath).getChannel();
                conn = new FileOutputStream(fftFile).getChannel();
                long size = url.size();
                MappedByteBuffer la = url.map(FileChannel.MapMode.READ_ONLY, 0, size);
                conn.write(la);
            } finally {
                if (url != null) url.close();
                if (conn != null) conn.close();
            }
        }
    }
}

