        if ((_cd != null) && (_cd.length > 0)) {
            HashSet aux = new HashSet(_cd.length);
            Collections.addAll(aux, _cd);
            ilo.setCc(aux);
        }
        if ((_bcc != null) && (_bcc.length > 0)) {
            HashSet aux = new HashSet(_bcc.length);
            Collections.addAll(aux, _bcc);
            ilo.setBcc(aux);
        }
        ilo.setSubject(method);
        Date now = new Date();
        ilo.setSentDate(now);
        File dir = new File(System.getProperty("user.home") + File.separator + "tmp");
        if (!dir.exists()) {
            dir.mkdir();
        }
        if ((messment != null) && (messment.size() > 0)) {
            for (int i = 0; i < messment.size(); i++) {
