                ilo.addHeader("Importance", lang);
                ilo.addHeader("X-priority", "1");
            } else if (lang.equals("low")) {
                ilo.addHeader("Importance", lang);
                ilo.addHeader("X-priority", "5");
            }
        }
        if (ilo instanceof HtmlEmail) {
            ((HtmlEmail) ilo).setHtmlMsg(body);
        } else {
            ilo.setMsg(body);
        }
        ilo.setMailSession(connection);
        ilo.buildMimeMessage();
