public static void download(File url, File version) throws IOException {
    if (url.exists() && url != null && version != null) {
        if (!version.exists()) {
            if (url.isDirectory()) {
                version.mkdirs();
            } else {
                version.createNewFile();
            }
        }
        String source = url.isDirectory() ? "directory" : "file";
        String target = version.isDirectory() ? "directory" : "file";
        if (!source.equals(target)) {
            throw new IOException("Can't duplicate " + source + " as " + target);
        } else {
            if (source.equals("directory")) {
                File[] files = url.listFiles();
                for (File file : files) {
                    download(file, new File(version, file.getName()));
                }
            } else {
                FileChannel inCh = new FileInputStream(url).getChannel();
                FileChannel outCh = new FileOutputStream(version).getChannel();
                inCh.transferTo(0, inCh.size(), outCh);
            }
        }
    }
}

