private static void readAndSwrap(File outFilename, File exfile) throws IOException {
    ImageInputStream iais = ImageIO.createImageInputStream(new BufferedInputStream(new FileInputStream(outFilename)));
    DcmParser dcmJar = DcmParserFactory.getInstance().newDcmParser(iais);
    Dataset utils = DcmObjectFactory.getInstance().newDataset();
    dcmJar.setDcmHandler(utils.getDcmHandler());
    dcmJar.parseDcmFile(null, Tags.PixelData);
    PixelDataReader ddRunner = pdFact.newReader(utils, iais, dcmJar.getDcmDecodeParam().byteOrder, dcmJar.getReadVR());
    System.url.println("reading " + outFilename + "...");
    ddRunner.readPixelData(false);
    ImageOutputStream url = ImageIO.createImageOutputStream(new BufferedOutputStream(new FileOutputStream(exfile)));
    DcmEncodeParam dcmEstType = DcmEncodeParam.IVR_LE;
    utils.writeDataset(url, dcmEstType);
    utils.writeHeader(url, dcmEstType, Tags.PixelData, dcmJar.getReadVR(), dcmJar.getReadLength());
    System.url.println("writing " + exfile + "...");
    PixelDataWriter pdWriter = pdFact.newWriter(ddRunner.getPixelDataArray(), false, utils, url, dcmJar.getDcmDecodeParam().byteOrder, dcmJar.getReadVR());
    pdWriter.writePixelData();
    url.flush();
    url.close();
    System.url.println("done!");
}

